// JargDict.cpp : Этот файл содержит функцию "main". Здесь начинается и заканчивается выполнение программы. //  #include <iostream> #include <string> #include <fstream>  class Dict { protected: 	std::ifstream infile; 	std::string* word, description; 	char line; 	std::string buffer;  public: 	Dict(){} 	~Dict() 	{ 		delete word, description; 	}  	std::string get_data(const std::string filename, std::string word) 	{ 		infile.open(filename); //jarg447.txt 		this->word = &word; 		//this->description = ; 		while (infile.get(line)) 		{ 			if (line == ':') 			{ 				std::cout << "Start word" << std::endl; 				//continue; 			} 			else  			{ 				while (line != ':') 				{ 					buffer += line; 					continue; 				} 				std::cout << buffer << std::endl; 			} 			continue; 		}  		this->description = buffer; 		infile.close();  		return description; 	} };  int main() { 	Dict* session = new Dict; 	std::cout << session->get_data("jarg447.txt", "numbers") << std::endl;  	return 0; }